    background: #222 !important
}
p, .tutorial_text {
    color: #ccc !important
}
a, .link_home_videosearch {
    color: #09f !important
}
a:hover, .link_home_videosearch:hover {
    color: #0ff !important
}
a:active {
    color: #0050dd !important
}
.navbar-default, .popup {
    background: #333 !important
}
#global-search-input, .popup-overlay, .tutorial-popup {
    background: #151515 !important;
    border-color: #666 !important;
    box-shadow: 0px 1px 24px 1px #000 !important;
    color: #ddd !important
}
.popup-overlay .close img {
    filter: invert(100%)
}
