/* Checkbox coloring */
input[type="checkbox"] {
    -moz-appearance: none !important;
    border: 1px solid #666 !important;
    background-color: #444 !important;
    transition: color 0.3s;
    min-width: 12px;
    min-height: 12px;
    margin-bottom: 0 !important
}
input[type="checkbox"]:not([disabled]):hover {
    box-shadow: 0 0 4px #0084ff !important;
    border-color: #0084ff !important
}
input[type="checkbox"]:checked {
    background: #444 url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" width="10px" height="10px" viewBox="1 3 12 12" ><path fill="rgb(200,200,200)" d="M 13.09,2.87 C 13.09,2.87 6.00,11.00 6.00,11.00 6.00,11.00 2.00,9.00 2.00,9.00 4.25,12.81 7.00,14.00 7.00,14.00 9.24,10.42 12.04,7.39 13.09,2.87 Z" /></svg>') no-repeat center center !important
}

/* Select boxes coloring */
select {
    -moz-appearance: none !important;
    background: #555 url("data:image/svg+xml;charset=utf-8,%3Csvg width='11' height='7' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M434.574 488L430.5 492.147 426.428 488 425 489.399 430.5 494.999 436 489.399z' transform='translate(-425 -488)' fill='%23cccccc' fill-rule='evenodd'/%3E%3C/svg%3E") no-repeat calc(100% - 5px) 50% !important;
    border: none !important;
    padding: 3px 20px 3px 3px !important;
    color: #ccc !important
}
select:hover {
    background: #444 url("data:image/svg+xml;charset=utf-8,%3Csvg width='11' height='7' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M434.574 488L430.5 492.147 426.428 488 425 489.399 430.5 494.999 436 489.399z' transform='translate(-425 -488)' fill='%23cccccc' fill-rule='evenodd'/%3E%3C/svg%3E") no-repeat calc(100% - 5px) 50% !important;
    color: #fff !important
}

}

