/*------ VIEWHANCE ADDON ------*/
@-moz-document url-prefix(moz-extension: //TYPE-UUID-OF-VIEWHANCE-ADDON-HERE/options.html) {
html {
    background: #222 !important
}
body, aside, .tip:hover {
    background: #333 !important;
    box-shadow: 0 0 4px #000 !important;
    color: #ddd !important
}
.tip:hover {
    border-color: #444 !important
}
h3 {
    color: #fff !important
}
aside button {
    filter: invert(80%) hue-rotate(180deg);
}
label:not(.checkbox) {
    color: #ddd !important
}
label:not(.checkbox):hover, a:not(.active) {
    color: #2cd !important
}
a.active {
    color: #fff !important
}
a:hover {
    color: #2ff !important
}
.sub-shortcuts input {
    background: linear-gradient(#666, #555) !important;
    color: #ddd !important;
    border-color: #888 !important
}
input[type="text"], textarea, .tip code, select {
    background: #444 !important;
    border-color: #555 !important;
    color: #ddd !important
}
input[type="text"]:focus, textarea:focus, select:hover {
    border-color: #888 !important;
}
input[type="checkbox"] + .checkbox {
    background: #444 !important;
    border-color: #555 !important
}
input[type="checkbox"]:checked + .checkbox {
    color: #ddd !important;
}
input[type="checkbox"] + .checkbox:hover {
    border-color: #888 !important
}

}
