+  transform: scale(1, 1);
+  opacity: 0.8 !important;
+}
+.PanelUI-subView toolbarseparator.proton-zap {
+  border-image: var(--panel-separator-zap-gradient) 1 !important;
+  opacity: 0.7 !important;
+}
