/** Auto-Hide Bookmarks Toolbar **/

#PersonalToolbar {
  --uc-bm-height: 20px; /* Might need to adjust if the toolbar has other buttons */
  --uc-bm-padding: 7px; /* Vertical padding to be applied to bookmarks */
  --uc-autohide-toolbar-delay: 600ms; /* The toolbar is hidden after 0.6s */

  /* 0deg = "show" ; 90deg = "hide" ;  Set the following to control when bookmarks are shown */
  --uc-autohide-toolbar-focus-rotation: 0deg; /* urlbar is focused */
  --uc-autohide-toolbar-hover-rotation: 0deg; /* cursor is over the toolbar area */
}

:root[uidensity="compact"] #PersonalToolbar {
  --uc-bm-padding: 1px;
}
:root[uidensity="touch"] #PersonalToolbar {
  --uc-bm-padding: 7px;
}

#PersonalToolbar:not([customizing]) {
  position: relative;
  margin-bottom: calc(0px - var(--uc-bm-height) - 2 * var(--uc-bm-padding));
  transform: rotateX(90deg);
  transform-origin: top;
  transition: transform 135ms linear var(--uc-autohide-toolbar-delay) !important;
  z-index: 1;
  /* The following properties should allow the themes with trasparent toolbars to work */
  background-color: transparent !important;
  background-repeat: no-repeat, no-repeat, var(--lwt-background-tiling);
  --uc-bg-y: calc(
    -2 * (
        var(--tab-block-margin) + var(--toolbarbutton-inner-padding) +
          var(--toolbarbutton-outer-padding)
      ) - var(--tab-min-height) - 16px - var(--bookmark-block-padding)
  );
  background-position: top left, top left,
    var(--lwt-background-alignment, top left);
  background-position-y: top, top, var(--uc-bg-y), var(--uc-bg-y),
    var(--uc-bg-y);
  background-image: var(--toolbar-bgimage),
    linear-gradient(var(--toolbar-bgcolor), var(--toolbar-bgcolor)),
    var(--lwt-header-image, var(--lwt-additional-images)) !important;
}

#PlacesToolbarItems > .bookmark-item,
#OtherBookmarks,
#PersonalToolbar > #import-button {
  padding-block: var(--uc-bm-padding) !important;
}

#nav-bar:focus-within + #PersonalToolbar {
  transition-delay: 100ms !important;
  transform: rotateX(var(--uc-autohide-toolbar-focus-rotation, 0));
}

#navigator-toolbox:hover > #PersonalToolbar {
  transition-delay: 100ms !important;
  transform: rotateX(var(--uc-autohide-toolbar-hover-rotation, 0));
}

#navigator-toolbox:hover > #nav-bar:focus-within + #PersonalToolbar {
  transform: rotateX(0);
}

/*** Hide some buttons for hover */

#nav-bar:not([customizing="true"])
  > #nav-bar-customization-target
  > #urlbar-container:not(:hover)
  > #urlbar:not([focused])
  > #urlbar-input-container
  > #page-action-buttons {
  opacity: 0;
}

#page-action-buttons {
  transition: opacity 0.15s ease;
}

#nav-bar:not([customizing="true"])
  > #nav-bar-customization-target
  > #urlbar-container:not(:hover)
  > #urlbar:not([focused])
  > #urlbar-input-container
  > #identity-box {
  opacity: 0;
  /*visibility: collapse;*/
}

#identity-box {
  /*transition: visibility 0.30s ease-in-out, opacity 0.30s ease-in-out;*/
  transition: opacity 0.15s ease !important;
}

#nav-bar:not([customizing="true"])
  > #nav-bar-customization-target
  > #urlbar-container:not(:hover)
  > #urlbar:not([focused])
  > #urlbar-input-container
  > #tracking-protection-icon-container {
  opacity: 0;
}

#tracking-protection-icon-container {
  transition: opacity 0.15s ease !important;
}

tab:not([pinned])[selected] .tab-context-line {
  max-width: -moz-available !important;
  margin-left: 0px !important;
  margin-right: 0px !important;
  margin-bottom: 0px !important;
  border-radius: 5px !important;
  -moz-box-ordinal-group: 2 !important;
  opacity: 1 !important;
}
tab:not([pinned], [selected]) .tab-context-line {
  max-width: 12px !important;
  height: 3px !important;
  margin-left: 12px !important;
  margin-bottom: 0px !important;
  -moz-box-ordinal-group: 2 !important;
  opacity: 1 !important;
}
tab:is([pinned])[selected] .tab-context-line {
  max-width: -moz-available !important;
  margin-left: 0px !important;
  margin-right: 0px !important;
  margin-bottom: 0px !important;
  border-radius: 5px !important;
  -moz-box-ordinal-group: 2 !important;
  opacity: 1 !important;
}
tab:is([pinned]):not([selected]) .tab-context-line {
  max-width: 12px !important;
  height: 3px !important;
  margin-left: 14px !important;
  margin-bottom: 0px !important;
  -moz-box-ordinal-group: 2 !important;
  opacity: 1 !important;
}

/*Center New Tab Button*/
#TabsToolbar .toolbarbutton-1 {
  margin-top: -1px !important;
  margin-left: -1px !important;
}
/*"tab_line": "#126e82"*/
#tabbrowser-tabs {
  --tab-line-color: var(--my-gradient, transparent) !important;
}
#tabbrowser-tabs {
  --uc-tabs-scrollbutton-border: 0px !important;
}
#scrollbutton-up,
#scrollbutton-down {
  border-block-width: var(--uc-tabs-scrollbutton-border, 0px) !important;
}

.tabbrowser-tab[multiselected]:-moz-lwtheme {
  color: var(--lwt-tab-text) !important;
  --tab-line-color: rgba(255, 255, 255, 0.24) !important;
}
.tabbrowser-tab[multiselected]
  > .tab-stack
  > .tab-background:is([multiselected]),
.tabbrowser-tab > .tab-stack > .tab-content:is([multiselected]) {
  background-color: #4d4dfe !important;
}

.tabbrowser-tab:hover
  > .tab-stack
  > .tab-background:not([selected="true"], [multiselected]) {
  background-color: #4d4dfe50 !important;
}
/* Reduce spacing between pinned and regular tabs */
#tabbrowser-tabs[haspinnedtabs]:not([positionpinnedtabs])
  > #tabbrowser-arrowscrollbox
  > .tabbrowser-tab[first-visible-unpinned-tab] {
  margin-inline-start: 0px !important;
}
/**Gradient Line on Tab **/
.tabbrowser-tab .tab-stack {
  padding-left: 1px !important;
}
.tabbrowser-tab .tab-background {
  margin-left: -1px !important;
  margin-right: -1px !important;
}
.tabbrowser-tab:not([pinned])[selected="true"] .tab-background {
  background-image: var(--my-gradient) !important;
  border-radius: 5px !important;
  background-color: #4d4dfe50 !important;
  margin-left: -1px !important;
  margin-right: -1px !important;
}
.tabbrowser-tab .tab-content {
  margin-top: 3px !important;
  height: 30px !important;
  background-image: linear-gradient(#9494943b, #0404043b), #9494943b;
}
.tabbrowser-tab:not([pinned], [multiselected])[selected="true"]
  .tab-content:not([multiselected]) {
  border-radius: 5px !important;
  background-color: #4d4dfe50 !important;
  margin-top: 3px !important;
  transition-duration: 0ms !important;
  backdrop-filter: saturate(35%) brightness(10%) !important;
}
.tabbrowser-tab:is([pinned]):not([selected="true"]) .tab-background {
  height: 32px !important;
}
.tabbrowser-tab:is([pinned])[selected="true"] .tab-background {
  background-image: var(--my-gradient) !important;
  background-color: #4d4dfe50 !important;
  border-radius: 5px !important;
}
.tabbrowser-tab:is([pinned]):not([selected="true"]) .tab-content {
  margin-top: 3px !important;
  height: 30px !important;
}
.tabbrowser-tab:is([pinned])[selected="true"]:not([multiselected])
  .tab-content {
  border-radius: 5px !important;
  margin-top: 3px !important;
  height: 30px !important;
  backdrop-filter: saturate(35%) brightness(10%) !important;
}
/*"Botón de cerrar pestaña"*/
.tab-close-button {
  margin-inline-start: 0px !important;
  width: 16px !important;
  height: 16px !important;
  padding: 3px !important;
  margin-inline-end: -5px !important;
  border-radius: 8px !important;
}
.tab-close-button:hover {
  background-color: var(--toolbarbutton-active-background) !important;
}
.tabbrowser-tab:not([pinned]):hover .tab-close-button {
  display: -moz-box !important;
}
@keyframes tab-line-anim {
  from {
    margin-right: var(--tab-min-width) !important;
  }
  to {
    margin-right: 0 !important;
  }
}
.tab-background::before {
  animation: tab-line-anim 1500ms !important;
}
/* Disable animation for selected and pinned tabs */
.tabbrowser-tab[pinned] > .tab-stack > .tab-background::before {
  animation: none !important;
}
/* Source file https://github.com/MrOtherGuy/firefox-csshacks/tree/master/chrome/tab_line_loading_indicator.css made available under Mozilla Public License v. 2.0See the above repository for updates as well as full license text. */

@keyframes tab-loading-line-anim {
  from {
    background-position-x: left !important;
  }
  to {
    background-position-x: right !important;
  }
}
.tabbrowser-tab[busy] > .tab-stack > .tab-background[selected]::before {
  background-size: 300% !important;
  background-image: linear-gradient(
    90deg,
    #9400ff,
    #ba00e5,
    #df06b9,
    #e62da7,
    #f13b95,
    #f64d86,
    #f85f7a,
    #fc7169,
    #f9845b,
    #e8ab52
  ) !important;
  opacity: 0.9;
  animation: tab-line-anim 1500ms,
    tab-loading-line-anim 800ms alternate infinite ease-in-out !important;
}
/* Hide the throbber all tabs*/
/*.tab-throbber{ display: none } 
.tab-icon-image[src]{ display: -moz-box }*/

/* Remove throbber from selected tab*/
.tab-throbber[selected] {
  display: none;
}
/* Search panel background gradient */
.search-panel-one-offs-container {
  --Search-engine-choices-gradient: linear-gradient(
    90deg,
    rgba(4, 34, 74, 1) 5%,
    rgba(22, 52, 132, 1) 50%,
    rgba(4, 34, 74, 1) 95%
  );
}

#main-menubar menu:not([disabled="true"])label {
  color: #ccc !important;
}

/* Selected tab lines */
.tabbrowser-tab[visuallyselected]::before,
.tabbrowser-tab[multiselected="true"]:not([visuallyselected])
  .tab-background::before {
  border-radius: 5px;
  content: "";
  background-color: var(--tab-line-color);
  min-height: var(--tab-min-height);
  background-image: linear-gradient(
    90deg,
    rgba(0, 0, 0, 0.6) 1%,
    transparent 50%,
    rgba(0, 0, 0, 0.6) 99%
  );
  clip-path: polygon(
    0 0,
    100% 0%,
    100% 100%,
    0% 100%,
    0% 88%,
    2% 95%,
    98% 95%,
    100% 85%,
    100% 20%,
    98.5% 6%,
    1.5% 6%,
    0% 20%
  );
  position: absolute;
  z-index: -1;
}

.tabbrowser-tab[visuallyselected]::before {
  top: 2px;
  left: 2px;
  bottom: 3px;
  right: 2px;
}

.tabbrowser-tab[multiselected="true"]:not([visuallyselected])
  .tab-background::before {
  min-height: var(--tab-min-height);
  width: 100%;
}

/* Tab separators */
.tabbrowser-tab::after {
  content: "";
  max-height: calc(var(--tab-min-height) - 4px);
  width: 1px;
  display: flex;
  margin-top: 6px;
}

#main-window[lwt-toolbar-field-brighttext]
  .tabbrowser-tab:not([visuallyselected]):not([beforeselected-visible]):not([before-multiselected]):not([multiselected="true"]):not([last-visible-tab])::after {
  box-shadow: 2px 0 0 0 rgba(255, 255, 255, 0.2) !important;
}

#main-window:not([lwt-toolbar-field-brighttext])
  .tabbrowser-tab:not([visuallyselected]):not([beforeselected-visible]):not([before-multiselected]):not([multiselected="true"]):not([last-visible-tab])::after {
  box-shadow: 2px 0 0 0 rgba(0, 0, 0, 0.2) !important;
}

/* Tabs background color when using a lightweight theme */
#main-window:not([lwt-default-theme-in-dark-mode])
  #tabbrowser-tabs:not([movingtab])
  .tab-background[selected="true"]:-moz-lwtheme {
  background-image: none !important;
  background-color: var(--lwt-selected-tab-background-color) !important;
}

/* Tab container lines */
.tabbrowser-tab .tab-context-line {
  margin-top: calc(var(--tab-min-height) - 6px) !important;
  clip-path: polygon(6% 0, 94% 0, 94% 100%, 6% 100%);
}
tab-icon-pending:not([selected]),
.tab-sharing-icon-overlay:not([selected]),
.tab-throbber[busy]:not([pinned]),
.tab-throbber:is([busy][pinned]),
.tab-icon-image:not([selected]) {
  box-shadow: 0 -1px 0px var(--identity-icon-color, #00000000),
    0 1px 0px var(--identity-icon-color, #00000000) !important;
  border-radius: 4px !important;
  padding: 2px !important;
  width: 20px !important;
  height: 20px !important;
}
.tab-icon-pending[selected],
.tab-sharing-icon-overlay[selected],
.tab-icon-image[selected] {
  outline: 1px solid var(--identity-icon-color, #00000000) !important;
  border-radius: 3px !important;
  padding: 2px !important;
  width: 20px !important;
  height: 20px !important;
}
